import time
def ltClump(seq, size, length, times):
    clump_words = {}
    i = 0
    while i < len(seq) - length:
        words = {}
        seq_slice = seq[i:i+length]
        for j in range(len(seq_slice)-size):
            word = seq_slice[j:j+size]
            if word in words:
                words[word] += 1
            else:
                words[word] = 1
        for word in words:
            if words[word] >= times:
                if word in clump_words:
                    clump_words[word] += 1
                else:
                    clump_words[word] = 1
        i += 1
    return len(clump_words.keys())

